import React from 'react'
import styled from 'styled-components'
import {
    UserAddOutlined,
} from '@ant-design/icons';
import UserAccounts from './userAccounts';
import { UserContext } from '../../providers/user.provider'

const Component = styled.div`
display:grid;
background:var(--secondary);
grid-template-rows: 1fr 62px;
padding: 0px 20px;
p{
    margin: 0;
}

.footer{
    display: grid;
}

.footer button{
    background: transparent;
    border: 0;
    text-decoration: underline;
    cursor: pointer;
}


.top{
    display: grid;
    grid-template-rows: 75px 1fr;
}

.circle{
    width: 35px;
    height: 35px;
    background: silver;
    border-radius: 50%;
}

.user-top-card{
    display: grid;
    grid-template-columns: 55px 1fr;
    align-items: center;
    background: #EDF2F7;
}

.account-list{
    padding-top: 50px;
}
`
export default function SideBar() {

    return (
        <Component>
            <div className="top">

                <div className="user-top-card">
                        <div className="circle"></div>

                        <div className="info">
                            <p>
                                King Somto
                            </p>
                        </div>
                </div>

               <div className="account-list">
               <UserAccounts  data= {{
                                 name:'the_real_bastard101',account:'twitter',accountId:'yo mama' 
                                }} />
               </div>
            </div>
            <div className="footer">
                <button>
                    <UserAddOutlined />    Add a social acount
                </button>
            </div>
        </Component>
    )
}
