import React, { useContext, useEffect, useState } from "react";
import { panelStateContext } from "../../../contexts/panelState";
import MainBaord from "../../mainboard/mainBoard";
import SideBar from "../../sidebar/sideBar";
import { useMediaQuery } from "react-responsive";
import "./dashboard.scss";
export default function Dashboard() {
  const [panelState, setPanelState] = useState(false);
  const isMobile = useMediaQuery({ query: `(min-width: 780px)` });
  return (
    <panelStateContext.Provider value={{ panelState, setPanelState }}>
      <div className="container">
        {}
        {panelState && !isMobile ? <SideBar /> : isMobile ? <SideBar /> : ""}
        <MainBaord />
      </div>
    </panelStateContext.Provider>
  );
}
