import React, {useEffect, createContext, useContext } from "react";
import styled from "styled-components";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import Close from "../assets/svg/close(1).svg";
import Cart from "../Components/Cart";
import CartObject from  '../utils/cart'
import { loadUser } from "../services/auth";

const userContext = createContext({ setCartShow: () => { } });

const Component = styled.div`
.loading{
   height: 100vh;
   width: 100vw;
   background: white;
   position: sticky;
   z-index: 4;
   text-align: center;
   padding-top: 40vh;
   top:0
}

`;


const UserProvider = ({ children,readyCart }) => {

  const [userData, setUserData] = React.useState(false);


  const holdAppLoadTillLoadUser = async ()=>{
   const data =  await loadUser()
   setUserData(data)
  }

  React.useEffect(()=>{
    ////aut load user
    holdAppLoadTillLoadUser()
  },[])




  return (
    <Component>
      <userContext.Provider value={{ userData }}>

        {children}
      </userContext.Provider>
    </Component>

  );
};

export const UserContext = () => useContext(userContext);

export default UserProvider;
