import { requestAuthToken } from "../services/auth"
import queryString from 'query-string';

/**
 * @description login util function 
 */
export const loginUtil = async () =>{
    const {data} =  await requestAuthToken()
    const  {oauth_token}  = data
    console.log({data})
    window.location.href = `https://api.twitter.com/oauth/authenticate?oauth_token=${oauth_token}`;
}

/**
 * @description validate the callback
 */
export const validateUtil = async () =>{
    const {oauth_token, oauth_verifier} = queryString.parse(window.location.search);  

    if (oauth_token && oauth_verifier) {
        try {
           //Oauth Step 3
           await axios({
             url: `${apiPath}/twitter/oauth/access_token`,  
             method: 'POST',
             data: {oauth_token, oauth_verifier}
           });
        } catch (error) {
         console.error(error); 
        }
       }

}
